-- ----------------------------
-- Table structure for terminal_app
-- ----------------------------
DROP TABLE IF EXISTS `terminal_app`;
CREATE TABLE `terminal_app`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
