    ');
   
  
SELECT @sql;
PREPARE state1 FROM @sql;
EXECUTE  state1; 
DEALLOCATE PREPARE state1; 
SELECT CONCAT('[',GROUP_CONCAT(DATA ORDER BY TYPE),']') INTO p_result FROM tmp01;
  
  
 ELSE IF p_timeType<4 AND p_timeType>0 THEN
   -- 数据中的日期 项
