declare stime datetime;-- 统计的开始日期（包含本身）
declare etime datetime;-- 统计的结束日期（不包含本身） 
declare v_total int;
set p_result:='{}';
if p_timetype =0 then
  set stime :=current_date()-interval 1 day;
