GROUP BY a.vehicleNo,stat_time,a.actiontypeDesc,driverName,a.begin_site;
-- 违规次数 统计   起始站点 为 -1  
INSERT INTO api_site_violation_month(deptId,vehicleNo,stat_date,actiontypeDesc,driverName,total,score,begin_site)
