CREATE PROCEDURE `p_driver_analysis_by_timerange`(p_start date,p_end date)
BEGIN
  -- 统计一个时间段的驾驶行为，用于初始化数据或重新计算
  declare v_day date;
  
  set v_day:=p_start;
  
  while v_day<=p_end do
    call p_driver_analyse_count_by_day(v_day);
		set v_day:=v_day+1;
  end while;
  
END
