  -- 获取车辆基本信息 
  select deviceid,vehicleNo,vehicleType,motorType,deptId,eleCost 
  into v_deviceId,v_vehicleNo,v_vehicleType,v_motorType,v_deptId,v_statndardCost
  from t_vehicleinfo where deviceId=p_deviceId;
  
