  end if;
  
  -- 获取加电量 
  select energyVolume into v_volumeAdd
  from t_energyinfo 
  where vehicleNo=v_vehicleNo
  and energyType='ele' ;
  
  select accruedmiles into v_totalmiles from t_vehiclemiles
  where vehicledeviceid=p_deviceId and stat_date=p_date;
  
  if v_totalmiles > 0 then
