  declare v_first_time datetime ;
  declare v_pre_vid varchar(256) ;
  declare v_pre_time datetime ;
  declare v_cur_vid varchar(256) ;
  declare v_cur_time datetime ;
  
    -- 新增 gps信息  2018年9月20日  by 韩亚璋
 DECLARE v_cur_gpsx VARCHAR(45);
