delimiter ;;
CREATE PROCEDURE `p_stat_motorlowcurrentalarm_byDay`(in p_date date)
BEGIN
-- ATS系统监控故障月报表 风扇1
  -- 算法描述：状态量2 中连续的一组1，则为 报警
  DECLARE v_first_vid VARCHAR(256) ;
