  OPEN cur01;
  FETCH cur01 INTO v_first_vid,v_first_time;
  SET v_pre_vid:=v_first_vid;
  SET v_pre_time:=v_first_time;
 
     
  label01:WHILE NOT @cursor_not_found DO
