	WHERE a.VEHICLEDEVICEID=v_first_vid
	AND a.time>v_pre_time AND a.time<v_cur_time LIMIT 1) t;
	
	
    IF v_cur_vid<>v_pre_vid OR v_cnt=1 THEN
    -- 不是同一辆车或相邻的两个候选不是连续的记录
