	ELSE 
	-- 算违规，但该次违规判断还未结束，继续判断下一条记录  
	  SET v_pre_time:=v_cur_time;
	   -- 新增 gps信息  2018年9月20日  by 韩亚璋
	  set v_pre_gpsx:=v_cur_gpsx;
	  set v_pre_gpsy:=v_cur_gpsy;
	END IF;
           
    
  END WHILE label01;
  
  CLOSE cur01;
END
