CLOSE cur01;
UPDATE stat_vehicleenergy SET energyStandard=151 WHERE  motorType=5  AND stat_date=CURRENT_DATE()-INTERVAL 1 DAY AND vehicleNo LIKE '辽%' ;
    END
;;
