CREATE PROCEDURE `p_zzf_countData`()
BEGIN
	#7分钟65条
	/*
	zhangzhufu生成统计数据
	2019年1月29日 12:19:27
	*/
	declare d_vehicledeviceid VARCHAR(256) ;
	declare d_time timestamp DEFAULT '0000-00-00 00:00:00';  
